import { useState, useEffect } from "react";
import React from 'react';
import './index.css';
import data from "./publications.json";


// компонента принимает пропсы active - отвечает, видна компонента или нет, и функция, которое это состояние изменяет
const [currentPage] = useState(1);
const [itemPerPage] = useState(2);

const indexOfLastItem = currentPage * itemPerPage;
const indexOfFirstItem = indexOfLastItem - itemPerPage;
const currentItems = data.slice(indexOfFirstItem, indexOfLastItem);

// вывод элементов в зависимости от ширины экрана
const [width, setWidth] = useState(window.innerWidth);
const breakpoint = 1024;
useEffect(() => {
 const handleResizeWindow = () => setWidth(window.innerWidth);
  // subscribe to window resize event "onComponentDidMount"
  window.addEventListener("resize", handleResizeWindow);
  return () => {
    // unsubscribe "onComponentDestroy"
    window.removeEventListener("resize", handleResizeWindow);
  };
}), [];
if (width < breakpoint) {
  setMobileActive(true);
};

const Mobile = ({active, setActive, id}) => {
    return (
        // вешаем обработчик, который будет менять состояние active на false (при нажатии на затемненную облать будет закрываться)
        // добавляем класс modal-post.active динамически с проверкой
        <div className={active ? "mobile-post active-post" : "mobile-post"}  id={id}>
                <div className="posts-card">
                    {currentItems.map((item) => (
                    <div className="post-item open-btn" data-id={item.id} key={item.id} id={item.id}
                    onClick={openCard}>
                        <div className="div-for-img-small-post">
                            <img className="post-item-img" src={item.imagePathSmall} alt=""/>
                        </div>

                        <p className="titleMain">{item.titleMain}</p>
                        <p className="title">{item.title.slice(0,100)}...</p>
                        <p className="date">{item.date}</p>
                    </div>
                    ))}
                </div>

        </div>
    )
}

export default Mobile;
